getSelectValues(select) {
  var result = [];
  var options = select && select.options;
  var opt;

  for (var i=0, iLen=options.length; i<iLen; i++) {
    opt = options[i];

    if (opt.selected) {
      result.push(opt.value || opt.text);
    }
  }
  return result;
}

uploadFiles( file ) {
  for ( let i = 0; i < file.length; i++ ) {
      this.formData.append( "file", file[i], file[i]['name'] );
  }
}


